import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './ChatConversation.css';  // Importing the CSS file

const BUDDY_API_URL = 'http://localhost:3003/buddies';

function ChatConversation() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const topic = queryParams.get('topic');
  const buddyId = parseInt(queryParams.get('buddyId'), 10);

  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [buddy, setBuddy] = useState(null);
  const [isTyping, setIsTyping] = useState(false);

  useEffect(() => {
    // Fetch buddy data from JSON server
    fetch(BUDDY_API_URL)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const selectedBuddy = data.find(b => b.id === buddyId);
        setBuddy(selectedBuddy);
        if (selectedBuddy && topic) {
          const initialMessages = [
            { text: `Hi! I'm ${selectedBuddy.name}. Let's dive into ${topic}.` },
            { text: selectedBuddy.topics[topic][0].response }
          ];
          setMessages(initialMessages);
        }
      })
      .catch(error => console.error('Fetching error:', error));
  }, [topic, buddyId]);

  const generateResponse = (userMessage) => {
    if (buddy && buddy.topics[topic]) {
      const lowerCaseMessage = userMessage.toLowerCase();
      for (const response of buddy.topics[topic]) {
        if (lowerCaseMessage.includes(response.trigger)) {
          return response.response;
        }
      }
      return "That's an interesting point! Tell me more.";
    }
    return "I'm not sure how to respond to that, but I'm here to listen!";
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (input.trim() === '') {
      return;
    }

    const userMessage = { text: input };
    setMessages([...messages, userMessage]);

    setIsTyping(true);

    setTimeout(() => {
      const nextResponse = generateResponse(input);
      setMessages(prevMessages => [...prevMessages, { text: nextResponse }]);
      setIsTyping(false);
    }, 1000);

    setInput('');
  };

  return (
    <div className="chat-conversation">
      <h1>Conversation on {topic}</h1>
      <div className="messages">
        {messages.map((msg, index) => (
          <p key={index} className="message">{msg.text}</p>
        ))}
        {isTyping && <p className="message typing">ChatBot is typing...</p>}
      </div>
      <form onSubmit={handleSubmit} className="input-form">
        <input 
          type="text" 
          value={input} 
          onChange={(e) => setInput(e.target.value)} 
          placeholder="Type a message..." 
          className="input-field"
        />
        <button type="submit" className="send-button">Send</button>
      </form>
    </div>
  );
}

export default ChatConversation;
