import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './ChatConversation.css';

function ChatConversation() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const topicId = queryParams.get('topic');

  const [buddyData, setBuddyData] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  useEffect(() => {
    // Fetch buddy data from db.json
    fetch('/db.json')
      .then(response => response.json())
      .then(data => {
        const buddy = data.buddies.find(b => b.id === parseInt(topicId));
        setBuddyData(buddy);
        if (buddy) {
          const greeting = buddy.greetings[Math.floor(Math.random() * buddy.greetings.length)];
          setMessages([{ text: greeting, sender: 'buddy' }]);
        }
      });
  }, [topicId]);

  const generateResponse = (userMessage) => {
    if (!buddyData) return;

    // Check for greetings
    const lowerUserMessage = userMessage.toLowerCase();
    if (buddyData.greetingTriggers.some(trigger => lowerUserMessage.includes(trigger))) {
      return buddyData.greetings[Math.floor(Math.random() * buddyData.greetings.length)];
    }

    // Check for farewells
    if (buddyData.farewellTriggers.some(trigger => lowerUserMessage.includes(trigger))) {
      return buddyData.farewells[Math.floor(Math.random() * buddyData.farewells.length)];
    }

    // Check for topics
    const topic = Object.keys(buddyData.topics).find(key => 
      lowerUserMessage.includes(key)
    );

    if (topic) {
      const response = buddyData.responses[topic][Math.floor(Math.random() * buddyData.responses[topic].length)];
      const didYouKnow = buddyData.didYouKnow[topic][Math.floor(Math.random() * buddyData.didYouKnow[topic].length)];
      return `${response}\n\nDid you know? ${didYouKnow}`;
    }

    // General response if no specific topic is detected
    return buddyData.generalResponses[Math.floor(Math.random() * buddyData.generalResponses.length)];
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim() === '') return;

    const userMessage = { text: input, sender: 'user' };
    setMessages(prevMessages => [...prevMessages, userMessage]);

    const buddyResponse = generateResponse(input);
    setMessages(prevMessages => [...prevMessages, { text: buddyResponse, sender: 'buddy' }]);

    setInput('');
    setCurrentQuestionIndex(prevIndex => (prevIndex + 1) % buddyData.questions.length);
  };

  const handleSuggestedQuestion = (question) => {
    setInput(question);
    handleSubmit({ preventDefault: () => {} });
  };

  if (!buddyData) return <div>Loading...</div>;

  return (
    <div className="chat-container">
      <div className="chat-header">
        <img src={buddyData.profilePic} alt={buddyData.name} className="profile-pic" />
        <h1>Chat with {buddyData.name}</h1>
      </div>
      <div className="message-container">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit} className="input-form">
        <input 
          type="text" 
          value={input} 
          onChange={(e) => setInput(e.target.value)} 
          placeholder="Type a message..." 
        />
        <button type="submit">Send</button>
      </form>
      <div className="suggested-questions">
        <h3>Suggested Questions:</h3>
        <button onClick={() => handleSuggestedQuestion(buddyData.questions[currentQuestionIndex])}>
          {buddyData.questions[currentQuestionIndex]}
        </button>
      </div>
    </div>
  );
}

export default ChatConversation;
