import React, { useState, useEffect, useRef } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import './ChatConversation.css';  // Importing the CSS file

function ChatConversation() {
  const { id } = useParams();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const topic = queryParams.get('topic');

  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const chatEndRef = useRef(null);

  useEffect(() => {
    fetch('/db.json')
      .then(response => response.json())
      .then(data => {
        setMessages(data.conversations[id] ? data.conversations[id][topic] : []);
      });
  }, [id, topic]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleResponse = (response) => {
    const currentFact = getAIResponse(null, topic); // Get a new fact to use

    if (response.trim().toLowerCase() === 'no') {
      setMessages(prevMessages => [
        ...prevMessages, 
        { text: getFactExplanation(currentFact, topic), sender: 'buddy' }
      ]);
    } else if (response.trim().toLowerCase() === 'yes') {
      setMessages(prevMessages => [
        ...prevMessages, 
        { text: "Very knowledgeable of you!", sender: 'buddy' },
        { text: getAIResponse(null, topic), sender: 'buddy' } // Provide another fact here
      ]);
    }

    setInput('');
  };

  const getAIResponse = (userInput, topic) => {
    const responses = {
      political: [
        "Did you know that the United Nations was founded in 1945?",
        "Did you know that democracy originated in ancient Greece?",
        "Did you know that the United States has a two-party system?",
        "Did you know that the European Union is a political and economic union?",
        "Did you know that the Cold War was a period of geopolitical tension between the US and USSR?"
      ],
      emotional: [
        "Did you know that expressing gratitude can increase happiness?",
        "Did you know that laughter triggers the release of endorphins?",
        "Did you know that music can influence your emotions?",
        "Did you know that talking to friends can reduce stress?",
        "Did you know that physical touch can lower blood pressure?"
      ],
      scientific: [
        "Did you know that water makes up about 71% of the Earth's surface?",
        "Did you know that the speed of light is approximately 299,792 kilometers per second?",
        "Did you know that humans share 60% of their DNA with bananas?",
        "Did you know that the universe is estimated to be 13.8 billion years old?",
        "Did you know that honey never spoils?"
      ]
    };

    const facts = responses[topic] || [];
    return facts[Math.floor(Math.random() * facts.length)];
  };

  const getFactExplanation = (fact, topic) => {
    const explanations = {
      political: {
        "Did you know that the United Nations was founded in 1945?": "The United Nations was established in 1945 to promote international cooperation and prevent future conflicts.",
        "Did you know that democracy originated in ancient Greece?": "Democracy, as a system of government, began in ancient Greece around the 5th century BC.",
        "Did you know that the United States has a two-party system?": "The US political system is primarily dominated by two parties: the Democratic Party and the Republican Party.",
        "Did you know that the European Union is a political and economic union?": "The EU is a union of 27 European countries that work together on economic and political matters.",
        "Did you know that the Cold War was a period of geopolitical tension between the US and USSR?": "The Cold War lasted from the end of World War II until the dissolution of the Soviet Union in 1991, characterized by political and military tension."
      },
      emotional: {
        "Did you know that expressing gratitude can increase happiness?": "Studies show that regularly expressing gratitude can enhance overall well-being and satisfaction.",
        "Did you know that laughter triggers the release of endorphins?": "Laughter releases endorphins, the body's natural feel-good chemicals, which help improve mood.",
        "Did you know that music can influence your emotions?": "Music can affect our emotions by altering brain activity and releasing chemicals like dopamine.",
        "Did you know that talking to friends can reduce stress?": "Socializing with friends can help alleviate stress by providing support and increasing feelings of belonging.",
        "Did you know that physical touch can lower blood pressure?": "Physical touch, such as hugging, can reduce stress and lower blood pressure through the release of oxytocin."
      },
      scientific: {
        "Did you know that water makes up about 71% of the Earth's surface?": "Approximately 71% of Earth's surface is covered by water, mostly in the oceans.",
        "Did you know that the speed of light is approximately 299,792 kilometers per second?": "Light travels at a speed of about 299,792 kilometers per second in a vacuum.",
        "Did you know that humans share 60% of their DNA with bananas?": "About 60% of human DNA is similar to that of bananas, reflecting fundamental biological processes.",
        "Did you know that the universe is estimated to be 13.8 billion years old?": "The age of the universe is estimated at approximately 13.8 billion years based on the cosmic microwave background radiation.",
        "Did you know that honey never spoils?": "Honey's low water content and high acidity prevent bacteria and microorganisms from growing, allowing it to remain edible indefinitely."
      }
    };

    return explanations[topic]?.[fact] || "Sorry, I don't have more information on this fact.";
  };

  return (
    <div className="chat-conversation">
      <h1>Conversation on {topic}</h1>
      <div className="chat-container">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            <div className={`chat-bubble ${msg.sender}`}>
              <p>{msg.text}</p>
            </div>
          </div>
        ))}
        <div ref={chatEndRef} /> {/* This div helps to scroll to the bottom */}
      </div>
      <form className="input-form" onSubmit={(e) => { e.preventDefault(); handleResponse(input); }}>
        <input 
          type="text" 
          className="input-field"
          value={input} 
          onChange={(e) => setInput(e.target.value)} 
          placeholder="Type your response..." 
        />
        <button type="submit" className="send-button">Send</button>
      </form>
    </div>
  );
}

export default ChatConversation;
