import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './ChatConversation.css';  // Importing the CSS file

function ChatConversation() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const topic = queryParams.get('topic');

  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [fetchedResponses, setFetchedResponses] = useState({});

  useEffect(() => {
    // Fetch responses from a JSON server or API
    fetch('http://localhost:3001/responses')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => setFetchedResponses(data))
      .catch(error => console.error('Fetching error:', error));
  }, []);

  useEffect(() => {
    // Initialize conversation
    if (fetchedResponses[topic]) {
      const initialMessage = `Let's talk about ${topic}.`;
      setMessages([{ text: initialMessage }]);
    }
  }, [topic, fetchedResponses]);

  const generateResponse = (userMessage) => {
    if (fetchedResponses[topic]) {
      for (const response of fetchedResponses[topic]) {
        if (userMessage.toLowerCase().includes(response.trigger)) {
          return response.response;
        }
      }
      return "That's an interesting point. What more can you share?";
    }
    return "I'm not sure how to respond to that.";
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (input.trim() === '') {
      return;
    }

    const userMessage = { text: input };
    setMessages([...messages, userMessage]);

    const nextResponse = generateResponse(input);
    setMessages(prevMessages => [...prevMessages, { text: nextResponse }]);

    setInput('');
  };

  return (
    <div>
      <h1>Conversation on {topic}</h1>
      <div>
        {messages.map((msg, index) => (
          <p key={index}>{msg.text}</p>
        ))}
      </div>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          value={input} 
          onChange={(e) => setInput(e.target.value)} 
          placeholder="Type a message..." 
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default ChatConversation;
