import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function ChatProfile() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log(`Fetching profile for buddy with id: ${id}`);
    fetch(`/buddies/${id}`)
      .then(response => {
        console.log(`Received response with status: ${response.status}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        console.log('Received data:', data);
        if (!data) {
          throw new Error('No data received from server');
        }
        setProfile(data);
      })
      .catch(err => {
        console.error("Fetch error:", err);
        setError(err.message);
      });
  }, [id]);

  const handleTopicClick = (topic) => {
    navigate(`/conversation/${id}?topic=${topic}`);
  };

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!profile) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{profile.name}</h1>
      {profile.profilePic && <img src={profile.profilePic} alt={profile.name} />}
      <p>Choose a conversation topic:</p>
      <ul>
        <li><button onClick={() => handleTopicClick('political')}>Political</button></li>
        <li><button onClick={() => handleTopicClick('emotional')}>Emotional</button></li>
        <li><button onClick={() => handleTopicClick('scientific')}>Scientific</button></li>
      </ul>
    </div>
  );
}

export default ChatProfile;
