import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import './ChatConversation.css';

function ChatConversation() {
  const { id } = useParams();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const topic = queryParams.get('topic');

  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [questions, setQuestions] = useState({
    personal: [],
    professional: [],
    random: []
  });
  const [questionIndex, setQuestionIndex] = useState({ personal: 0, professional: 0, random: 0 });
  const [currentCategory, setCurrentCategory] = useState('personal');
  //const [categoryOrder, setCategoryOrder] = useState(['personal', 'professional', 'random']);

  useEffect(() => {
    // Fetch buddy's questions categorized by topic
    fetch(`http://localhost:3000/buddies/${id}`)
      .then(response => response.json())
      .then(data => {
        setQuestions({
          personal: data.personalQuestions || [],
          professional: data.professionalQuestions || [],
          random: data.randomQuestions || []
        });
      });
  }, [id]);

  const getNextCategory = () => {
    const currentIndex = categoryOrder.indexOf(currentCategory);
    const nextIndex = (currentIndex + 1) % categoryOrder.length;
    return categoryOrder[nextIndex];
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Add user's message
    const newMessage = { text: input };
    setMessages([...messages, newMessage]);

    // Display the next question from the current category
    const currentQuestions = questions[currentCategory];
    const index = questionIndex[currentCategory];

    if (index < currentQuestions.length) {
      const nextQuestion = currentQuestions[index];
      setMessages(prevMessages => [...prevMessages, newMessage, { text: nextQuestion }]);
      setQuestionIndex(prevIndex => ({
        ...prevIndex,
        [currentCategory]: prevIndex[currentCategory] + 1
      }));
    }

    // Switch to the next category for the next question
    const nextCategory = getNextCategory();
    setCurrentCategory(nextCategory);

    setInput('');
  };

  return (
    <div>
      <h1>Conversation on {topic}</h1>
      <div>
        {messages.map((msg, index) => (
          <p key={index}>{msg.text}</p>
        ))}
      </div>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          value={input} 
          onChange={(e) => setInput(e.target.value)} 
          placeholder="Type a message..." 
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default ChatConversation;
