import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function ChatProfile() {
  const { id } = useParams();
  const navigate = useNavigate();

  React.useEffect(() => {
    console.log(`Fetching profile for buddy with id: ${id}`);
    fetch(`/buddies/${id}`)
      .then(response => {
        console.log(`Received response with status: ${response.status}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        console.log('Received data:', data);
        if (!data) {
          throw new Error('No data received from server');
        }
        // TODO: Use the data to update your component's state
      })
      .catch(err => {
        console.error("Fetch error:", err);
        // TODO: Handle the error (e.g., show an error message to the user)
      });
  }, [id]);

  const handleTopicClick = (topic) => {
    navigate(`/conversation/${id}?topic=${topic}`);
  };

  return (
    <div>
      <h1>Chat Profile</h1>
      <p>Loading profile for buddy with id: {id}</p>
      <p>Choose a conversation topic:</p>
      <ul>
        <li><button onClick={() => handleTopicClick('political')}>Political</button></li>
        <li><button onClick={() => handleTopicClick('emotional')}>Emotional</button></li>
        <li><button onClick={() => handleTopicClick('scientific')}>Scientific</button></li>
      </ul>
    </div>
  );
}

export default ChatProfile;
